import React, { useState } from 'react';


function Card (props) {

    const{changeNumber} = props;
    const[text,changeText] = useState("Add To Cart");

    const handleStateChange = () => {
        changeNumber();
    }

    const handleChange = () => {
        changeText({text:"Added To Cart"})
    }

    return(
        <div className='cards'>
            <div className='card'>
                <img src = {props.imgsrc} alt='loading..' className='card_img'/>
                <div className='card_info'>
                    <h3 className='card_title'>{props.bookName}</h3>
                    <span className='card_category'>{props.authorName}</span>
                    <h4 className='card_price'> â‚¹ {props.price}</h4>
                    <div className='button'>
                    <button className='button-cart' onChange={handleChange} onClick = {handleStateChange}>{text}</button>
                    <button className='button-wishlist'>Wishlist</button>
                    </div>
                </div>
            </div>

        </div>
    );

}
export default Card;
