import React from "react";
import loginImg from "../../images/login.svg";
import './BookStore-Style.scss';

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      id:"",
      name:"",
      number:"",
      email:"",
      errorMessage:false,
      textError:"",
      numberError:"",
      emailError:""
  }
  }
  handleChange=(e)=>{
    this.setState({
        [e.target.name]:e.target.value
    });
    let regex=RegExp("^[A-Z]{1}[a-zA-Z\\s]{2,}$");
    if(!regex.test(e.target.value)){
      this.setState({
        textError:"Invalid Name Format",
        errorMessage:true
      })
    }
    else{
      this.setState({textError:""});
    }
}

  render() {
    return (
      <div className="base-container" ref={this.props.containerRef}>
        <div className="header">Register</div>
        <div className="content">
          <div className="image">
            <img src={loginImg} />
          </div>
          <div className="form">
            <div className="form-group">
              <label htmlFor="username">Name</label>
              <input type="text" name="username" value={this.state.name} onChange={(e)=>{this.handleChange(e)}} placeholder="Username" />
              <error-output className="text-error" htmlFor="error">{this.state.textError}</error-output>
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="email" name="email" placeholder="Email" />
            </div>
            <div className="form-group">
              <label htmlFor="phoneNumber">Phone Number</label>
              <input type="text" name="email" placeholder="Phone Number" />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input type="password" name="password" placeholder="Password" />
            </div>
          </div>
        </div>
        <div className="footer">
          <button type="button" className="btn">
            Register
          </button>
        </div>
      </div>
    );
  }
}
export default Register;
