import React from 'react';
import '../bookStore-cart/CustomerForm.scss';
import {TextField} from '@material-ui/core';
import { TextareaAutosize } from '@material-ui/core';
import StoreService from '../../service/StoreService';
class CustomerForm extends React.Component{
    constructor(props){
        super(props);
        this.state={
            id:"",
            name:"",
            phoneNumber:"",
            pinCode:"",
            locality:"",
            address:"",
            town:"",
            landmark:"",
            type:""
        }
    }
    componentDidMount = () => {
        // let id = this.props.match.params.id;
        // if(id !== undefined && id !==''){
        //     new StoreService().getCustomerById(id)
        //     .then(responseDTO => {
        //         let responseData = responseDTO.data;
        //         this.setCustomerForm(responseData.data);
        //     }).catch(error => {
        //         console.log("Error while Fetching Data"+JSON.stringify(error));
        //     })
        // }
    }
    setCustomerForm=(CustomerData)=>{
        this.setState({
            id:CustomerData.id,
            name:CustomerData.name,
            phoneNumber:CustomerData.phoneNumber
        });
    }
    handleChange = (e) => {
        this.setState({
            [e.target.name]:e.target.value
        })
    }
    save=async(e)=>{
        e.preventDefault();
        if(this.state.errorMessage===true){
            window.alert("Please enter valid data");
        }
        else{
            let customerObject={
                id:this.state.id,
                pinCode:this.state.pinCode,
                locality:this.state.locality,
                address:this.state.address,
                town:this.state.town,
                landmark:this.state.landmark,
                type:this.state.type
            }
            new StoreService().updateUser(customerObject)
            .then(response=>{
                console.log("Data Updated Successfully"+JSON.stringify(response.data));
            })
            .catch(error=>{
                console.log("Error in Adding Customer's data"+JSON.stringify(error));
            })
        }
    }
    render(){
        return(
            <div className="main-container">
                <h3 className="form-title">Customer Details</h3>
               <form className='customerform' action="#" onSubmit={(e)=>this.save(e)} >
                   <div className="form-container">
                       <div className="form-input">
                            <div className="row">
                                <TextField className="text-input" type="text" label="Name" variant='outlined' style={{marginRight: '1.5%'}} size='medium'/>
                                <TextField className="text-input" type="text" variant='outlined' label="Phone Number" />
                            </div>
                            <div className="row">
                                <TextField className="text-input" type="text" label="Pincode" variant='outlined' style={{ marginRight: '1.5%'}}/>
                                <TextField className="text-input" type="text" label="Locality" variant='outlined' onChange={(e) => this.handleChange(e)}/>
                            </div>
                            <div className="row">
                                <TextField className="textarea" label="Address" variant='outlined' multiline={true} maxRows={5} onChange={(e) => this.handleChange(e)} />
                            </div>
                            <div className="row">
                                <TextField className="text-input" type="text" label="city/town" variant='outlined' onChange={(e) => this.handleChange(e)} style={{ marginRight: '1.5%'}}/>
                                <TextField className="text-input" type="text" label="Landmark" variant='outlined' onChange={(e) => this.handleChange(e)}/>
                            </div>
                            <div className="radio-row">
                                <label className="type-label">Type</label><br/>
                                <input className="radio-input" name="radio"type="radio" onChange={(e) => this.handleChange(e)}></input>
                                <label className="radio-label">Home</label>
                                <input className="radio-input radio" name="radio" type="radio" onChange={(e) => this.handleChange(e)}></input>
                                <label className="radio-label">Work</label>
                                <input className="radio-input radio" name="radio" type="radio" onChange={(e) => this.handleChange(e)}></input>
                                <label className="radio-label">Others</label>
                            </div>
                       </div>
                       <div className="button-input">
                           <button onClick={(e)=>this.props.togglePanelSummary(e)}className="continue-button"type="submit">Continue</button>
                       </div>
                   </div>
                </form> 
            </div>
        );
    }
    
}
export default CustomerForm
