import React, { useEffect } from 'react';
import './CartPage.scss';
import { useState } from 'react';
class CartPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            cartDetails : [],
        };
    } 

    componentDidMount(){
     this.setCartItems();
    }

    setCartItems = () => {
        this.setState( prevState => ({
            cartDetails: [
              ...prevState.cartDetails,
              JSON.parse(localStorage.getItem('cartDetails'))
            ]
          }))
          window.alert(JSON.parse(this.state.cartDetails))
    }
    render(){
        return(
            <div className="main">
            <div className="cart">
            <div className="cart-container">
             {
                 this.state.cartDetails.map((books) => {
                     <div className="book-container" key={books.bookId}>
                        <img className='book-cover' src={books.imgsrc} alt="" />
                        <p>{books.authorName}</p>
                     </div>
                 })
             }
            </div>
            <div className="button-container">
                <button className="place-order" onClick={(e)=>this.props.togglePanel(e)}>PLACE ORDER</button>
            </div>
        </div>
        </div>
        );
    }
}

export default CartPage;
