import React, { useEffect } from 'react';
import './CartPage.scss';
import { useState } from 'react';
class CartPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            cartDetails : [],
        };
    } 

    componentDidMount(){
        this.setState( prevState => ({
            cartDetails: [
              ...prevState.cartDetails,
              JSON.parse(localStorage.getItem('cartDetails'))
            ]
          }),
          console.log(this.state.cartDetails))
          
    }

    render(){
        return(
        <div className="main">
           {
                this.state.cartDetails.map((books) => (
                    <div key={books.bookId} className="book-container">
                        <h4 className='cart-title'>My Cart(1)</h4>
                        <img className='book-cover' src={books.imgsrc} alt="" />
                        <h4 className='book-title'>{books.bookName}</h4>
                        <p className='author-name'>by {books.authorName}</p>
                        <h4>Rs. {books.price}</h4>
                        <span className='controls'>
                            <div className="quantity">
                                 <button disabled={true}>-</button>
                                 <input type="text" defaultValue={1} contentEditable={false}/>
                                 <button>+</button>
                            </div>
                            <h6>Remove</h6>
                        </span>
                    </div>
                 
        ))
            }
        </div>
        );
    }
}

export default CartPage;
