import React, { useEffect } from 'react';
import './CartPage.scss';
import { useState } from 'react';
class CartPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            cartDetails : [],
        };
    } 

    render(){
        return(
            <div className="main">
            <div className="cart">
            <div className="cart-container">
                <div className="book-container">
                    <h4 className='cart-title'>My Cart(1)</h4>
                    <img className='book-cover' src="" alt="" />
                </div>
                <div className='book-info'>
                    <h4 className='book-title'>"Hello"</h4>
                    <p className='author-name'>by "Me"</p>
                    <h4>Rs. 1500</h4>
                    <span className='controls'>
                        <div className="quantity">
                             <button disabled={true}>-</button>
                             <input type="text" defaultValue={1} contentEditable={false}/>
                             <button>+</button>
                        </div>
                        <h6>Remove</h6>
                    </span>
                </div>
            </div>
            <div className="button-container">
                <button className="place-order" onClick={(e)=>this.props.togglePanel(e)}>PLACE ORDER</button>
            </div>
        </div>
        </div>
        );
    }
}

export default CartPage;
