import React, { useEffect } from 'react';
import './CartPage.scss';
import { useState } from 'react';
class CartPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            cartDetails : [],
        };
    } 

    componentDidMount(){
        this.setState( prevState => ({
            cartDetails: [
              ...prevState.cartDetails,
              JSON.parse(localStorage.getItem('cartDetails'))
            ]
          }),
          console.log(this.state.cartDetails))
          
    }

    render(){
        return(
        <div className="main">
           {
                this.state.cartDetails.map((books) => (
                 <div key = {books.bookId}>
                     <p>{books.authorName}</p>
                 </div>
                 
        ))
            }
        </div>
        );
    }
}

export default CartPage;
