import config from '../config/config';
import AxiosService from './AxiosService';


export default class StoreService{
    baseUrl = config.baseUrl;
    
    getAllBooks() {
        return AxiosService.getService(`${this.baseUrl}bookstore/get`);
    }
    addUser(){
        return AxiosService.postService(`${this.baseUrl}user/register`);
    }
    getCustomerById(UserId){
        return AxiosService.getService(`${this.baseUrl}user/get/${UserId}`);
    }

    updateUser(customerData){
        return AxiosService.putService(`${this.baseUrl}user/update/${customerData.id}`,customerData)
    }
}
