import React, { useState } from 'react';


function Card (props) {
    const[text,setText] = useState("Add To Cart");
    const[wishlistButton, setWishlistButton] = useState(true);

    const handleStateChange = () => {
        localStorage.setItem("number",1);
        setText("Added");
        setWishlistButton(false);
    }

    return(
        <div className='cards'>
            <div className='card'>
                <img src = {props.imgsrc} alt='loading..' className='card_img'/>
                <div className='card_info'>
                    <h3 className='card_title'>{props.bookName}</h3>
                    <span className='card_category'>{props.authorName}</span>
                    <h4 className='card_price'> â‚¹ {props.price}</h4>
                    <div className='button'>
                    <button className='button-cart' onClick = {handleStateChange}>{text}</button>
                    <div className="button-wishlist">
                        {wishlistButton && <button>Wishlist</button>}
                    </div>
                    </div>
                </div>
            </div>
        </div>
    );

}
export default Card;
